const getSvgBackgroundForCustom = (svg: string, cssFillColor: string, cssBgColor: string) => {
  const fillColor = getComputedStyle(document.documentElement).getPropertyValue(cssFillColor).trim();
  const bgColor = getComputedStyle(document.documentElement).getPropertyValue(cssBgColor).trim();
  const svgWithFill = svg.replace(/{fillColor}/g, fillColor).replace(/{bgColor}/g, bgColor);
  return `url("data:image/svg+xml;charset=UTF-8,${encodeURIComponent(svgWithFill)}")`;
};

const getSvgBackground = (faIcon: any, cssColorVar: string) => {
  const fillColor = getComputedStyle(document.documentElement).getPropertyValue(cssColorVar).trim();
  const iconData = icon(faIcon).abstract[0];
  const svgString = `<svg xmlns="http://www.w3.org/2000/svg" viewBox="${iconData.attributes.viewBox}" fill="${fillColor}"><path d="${iconData.children![0].attributes.d}" /></svg>`;
  return `url("data:image/svg+xml;charset=UTF-8,${encodeURIComponent(svgString)}")`;
};

