const cssHoneycombGrid = css`
  /* colors can be overrided */
  --backgroundColor: transparent;
  --borderColor: transparent;

  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  --sqrt3: ${sqrt3};
  --outerEdge: calc(var(--edge) + var(--border));

  /* use first outer as placeholder */
  --placeholderX: calc((var(--outerEdge) * var(--sqrt3) + var(--gap)) * 2);
  --placeholderY: calc(var(--outerEdge) * 3 + var(--gap) * var(--sqrt3));
  --centerShiftX: calc(
    (var(--outerEdge) / 2 * var(--sqrt3) - var(--gap) / 2) / 2
  );
  --centerShiftY: calc((var(--outerEdge) - var(--gap) * var(--sqrt3)) / 4);
  --polygon: polygon(
    0% 25%,
    50% 0%,
    100% 25%,
    100% 75%,
    50% 100%,
    0% 75%,
    0% 25%
  );
  min-width: var(--placeholderX);
  padding: var(--centerShiftY) var(--centerShiftX);
  /* outer */
  & > * {
    width: 0;
    height: 0;
    position: relative;
    /* middle */
    & > * {
      --width: calc(var(--outerEdge) * var(--sqrt3));
      min-width: var(--width);
      width: var(--width);
      height: calc(var(--outerEdge) * 2);
      --transform: translate(
        calc(0px - var(--centerShiftX)),
        calc(0px - var(--centerShiftY))
      );
      clip-path: var(--polygon);
      background-color: var(--backgroundColor);      
      position: absolute;
      display: flex;
      justify-content: center;
      align-items: center;
      /* background */
      & > span {           
        width: calc(var(--edge) * var(--sqrt3));
        height: calc(var(--edge) * 2);
        clip-path: var(--polygon);
        position: absolute;
        inset: calc(var(--gap) / 2 + var(--border))
          calc((var(--gap) / 2 + + var(--border)) * var(--sqrt3) / 2);
      }
      /* inner */
      & > div {      
        overflow: hidden;
        display: flex;
        align-items: center;
        justify-content: center;
        width: calc(var(--edge) * var(--sqrt3));
        font-size: ${fontSize};
      }
      & > div,
      &:before,
      &:after {
        content: "";
        position: absolute;
        color: var(${iconColor});
        bottom: 0;
        height: 50%;
        width: calc(var(--edge) * var(--sqrt3));
        border-width: 0 var(--border);
        border-style: solid;
        border-color: var(--borderColor);
      }
      &:before {
        transform: rotate(60deg);
        z-index: -1;
      }
      &:after {
        transform: rotate(120deg);
        z-index: -1;
      }
    }
    /* placeholder */
    &:nth-child(4n + 1) {
      width: var(--placeholderX);
      height: var(--placeholderY);
    }
    &:nth-child(4n + 1) > * {
      transform: var(--transform);      
    }
    &:nth-child(4n + 2) > * {
      transform: translate(calc(0px - var(--placeholderX) / 2)) var(--transform);
    }
    &:nth-child(4n + 3) > * {
      transform: translate(
          calc(0px - var(--placeholderX) / 4 * 3),
          calc(var(--placeholderY) / 2)
        )
        var(--transform);
    }
    &:nth-child(4n + 4) > * {
      transform: translate(
          calc(0px - var(--placeholderX) / 4),
          calc(var(--placeholderY) / 2)
        )
        var(--transform);
    }
  }
`;

export default function TechStackHoneyComb() {
  const [faBackgrounds, setFaBackgrounds] = useState<string[]>([]);
  const [customBackgrounds, setCustomBackgrounds] = useState<string[]>([]);

  const updateBackgrounds = () => {
    const newFaBackgrounds = faIcons.map((tech) => getSvgBackground(tech.icon, iconColor));
    setFaBackgrounds(newFaBackgrounds);

    const newCustomBackgrounds = customSvgIcons.map((tech) => getSvgBackgroundForCustom(tech.svg, iconColor, bgColor));
    setCustomBackgrounds(newCustomBackgrounds);
  };

  useEffect(() => {
    updateBackgrounds();

    const observer = new MutationObserver(() => {
      updateBackgrounds();
    });

    const targetNode = document.documentElement;
    observer.observe(targetNode, { attributes: true, attributeFilter: ['theme'] });

    return () => observer.disconnect();
  }, []);

  const cssContainer = css`
    --edge: 5em;
    --border: 3px;
    --gap: 0.2em;

    @media (max-width: 640px) {
      --edge: 4.5em;
      --border: 2px;
      --gap: 0.2em;
    }

    @media (max-width: 400px) {
      --edge: 4em;
      --border: 2px;
      --gap: 0.2em;
    }
  `;

  const containerVariants = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const childVariants = {
    hidden: { opacity: 0, y: 100 },
    show: { opacity: 1, y: 0, transition: { duration: 0.2, ease: 'circOut' } },
  };

  return (
    <motion.div
      className={cx(cssHoneycombGrid, cssContainer, cssDemo)}
      variants={containerVariants}
      initial="hidden"
      whileInView="show"
      viewport={{ once: true, amount: 0.35 }}
    >
      {faIcons.map((fa, i) => (
        <motion.div
          key={fa.name}
          variants={childVariants}
        >
          <div>
            <span
              style={{
                backgroundImage: faBackgrounds[i],
                backgroundSize: '3.5rem 3.5rem',
                backgroundPosition: 'center 40%',
                backgroundRepeat: 'no-repeat',
              }}
            />
            <div>{fa.name}</div>
          </div>
        </motion.div>
      ))}

      {customSvgIcons.map((svgIcon, i) => (
        <motion.div
          key={svgIcon.name}
          variants={childVariants}
        >
          <div className="">
            <span
              style={{
                backgroundImage: customBackgrounds[i],
                backgroundSize: '3.5rem 3.5rem',
                backgroundPosition: 'center 40%',
                backgroundRepeat: 'no-repeat',
              }}
            />
            <div>{svgIcon.name}</div>
          </div>
        </motion.div>
      ))}
    </motion.div>
  );
}
